{"operationName":"stackupTool","variables":{"slug":"wordpress"},"query":"query stackupTool($slug: ID!) {
  tool(id: $slug) {
    id
    legacyThirdPartyId
    slug
    contactEnabled
    contactButtonText
    contactFlow
    name
    imageUrl
    thumbUrl
    thumbRetinaUrl
    canonicalUrl
    stacks
    votes
    jobs
    followers {
      count
      __typename
    }
    following
    hackernewsOnlineMentionsCount
    redditOnlineMentionsCount
    stackOverflowOnlineMentionsCount
    githubForksCount
    githubStarsCount
    githubUpdatedAt
    description
    pricingUrl
    popularity
    path
    pressUrl
    relatedStackups {
      count
      __typename
    }
    layer {
      name
      slug
      __typename
    }
    category {
      name
      slug
      __typename
    }
    function {
      name
      slug
      __typename
    }
    pros(first: 7) {
      pageInfo {
        hasNextPage
        endCursor
        __typename
      }
      edges {
        node {
          ...reasonFields
          __typename
        }
        __typename
      }
      __typename
    }
    cons(first: 7) {
      pageInfo {
        hasNextPage
        endCursor
        __typename
      }
      edges {
        node {
          ...reasonFields
          __typename
        }
        __typename
      }
      __typename
    }
    companyStacksUsing(first: 7) {
      pageInfo {
        hasNextPage
        endCursor
        __typename
      }
      edges {
        node {
          ...companyStackFields
          __typename
        }
        __typename
      }
      __typename
    }
    allToolIntegrations(first: 7) {
      pageInfo {
        hasNextPage
        endCursor
        __typename
      }
      edges {
        node {
          ...stackupServiceFields
          __typename
        }
        __typename
      }
      __typename
    }
    officialArticles(first: 5) {
      edges {
        node {
          title
          importSource
          outboundUrl
          discussionScore
          publishedAt
          commentCount
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}

fragment companyStackFields on Stack {
  canonicalUrl
  owner {
    ... on Company {
      name
      imageUrl
      thumbUrl
      __typename
    }
    __typename
  }
  __typename
}

fragment stackupServiceFields on Tool {
  name
  imageUrl
  thumbUrl
  path
  __typename
}

fragment reasonFields on Reason {
  id
  upvoted
  upvotesCount
  text
  __typename
}
"}
