{"operationName":"bonusRubles","variables":{"products":[]},"query":"query bonusRubles($products: [BonusesProductRequestParameter]!) {
  bonusRubles(products: $products) {
    productId
    hasPromotionBonuses
    total {
      amount
      text
      __typename
    }
    breakdown {
      amount
      text
      __typename
    }
    __typename
  }
}
"}
