{"operationName":"menu","variables":{},"query":"query menu {
  menu {
    promo {
      ...categoryMenu
      categories {
        ...subCategory
        __typename
      }
      __typename
    }
    categories {
      ...categoryMenu
      categories {
        ...subCategory
        imageUrl
        __typename
      }
      __typename
    }
    __typename
  }
}

fragment categoryMenu on CategoryMenu {
  promoLinks {
    imageUrl
    url
    __typename
  }
  name
  imageUrl
  url
  showAllLink {
    name
    url
    __typename
  }
  __typename
}

fragment subCategory on CategoryMenu {
  name
  url
  categories {
    name
    url
    __typename
  }
  __typename
}
"}
